+        let pos_delta: Duration = day - monday;
+        // Check reciprocity
+        if day_num % 7 == 0 {
+            assert_eq!(pos_delta + neg_delta, Duration::ZERO);
+        } else {
+            assert_eq!(pos_delta + neg_delta, 7 * Unit::Day);
+        }
+        // Check actual value
