            setIsConnected(true);
            socket.emit('join_python_discovery', 'react');
            socket.emit('join_chat_group', 'react');
        });

        socket.on('disconnect', () => {
            setIsConnected(false);
        });
        socket.on('Start1234', function (data) {
            console.log(data, 'hello');
        });
        socket.on('hello', function (data) {
            console.log(data, 'hello');
        });
        // socket.on('pong', () => {
        //  setLastPong(new Date().toISOString());
        // });

        socket.emit('my message', `{name:'santhosh'}`);

        return () => {
            socket.off('connect');
            socket.off('disconnect');
            socket.off('hello');
            socket.off('Start1234');
        };
    }, []);

    return (
        <div className="App">
            <h1>UI</h1>
        </div>
    );
}

export default App;

```

**Expected behavior**
Server should emit hello event every 10s and it should be received on the UI

**Platform:**
 - Device: MAC
 - OS: iOS

