package main

import (
	"github.com/lesismal/nbio"

	"github.com/lesismal/nbio/logging"
)

func main() {

	// on macOS, if I do not set a value, program fail at engine_unix.go:131
	// with: panic: runtime error: makeslice: len out of range
	nbio.MaxOpenFiles = 6 * 1024 * 1024

	logging.SetLevel(logging.LevelInfo)
	g := nbio.NewGopher(nbio.Config{
		Network: "unix",
		Addrs:   []string{"/tmp/nbio.sock"},
	})

	g.OnData(func(c *nbio.Conn, data []byte) {
		if _, err := c.Write(append([]byte{}, data...)); err != nil {
			logging.Error("write error: %v", err)
		}
	})
	g.OnOpen(func(c *nbio.Conn) {
		logging.Info("client %d connected", c.Hash())
	})
	g.OnClose(func(c *nbio.Conn, err error) {
		logging.Warn("client %d disconnected, err: %v", c.Hash(), err)
	})
	g.OnStop(func() {
		logging.Info("stopping server")
	})

	err := g.Start()
	if err != nil {
		logging.Error("starting server failed: %v", err)
		return
	}
	defer g.Stop()

	g.Wait()
}
```

