package main

import (
	"bytes"
	"context"
	"math/rand"
	"time"

	"github.com/lesismal/nbio"
	"github.com/lesismal/nbio/logging"
)

func main() {
	var (
		ret    []byte
		buf    = make([]byte, 1024*1024*4)
		addr   = "/tmp/nbio.sock"
		ctx, _ = context.WithTimeout(context.Background(), 60*time.Second)
	)

	logging.SetLevel(logging.LevelInfo)

	rand.Read(buf)

	// on macOS, if I do not set a value, program fail at engine_unix.go:131
	// with: panic: runtime error: makeslice: len out of range
	nbio.MaxOpenFiles = 6 * 1024 * 1024

	g := nbio.NewGopher(nbio.Config{})

	done := make(chan int)
	g.OnData(func(c *nbio.Conn, data []byte) {
		ret = append(ret, data...)
		if len(ret) == len(buf) {
			if bytes.Equal(buf, ret) {
				close(done)
			}
		}
	})

	err := g.Start()
	if err != nil {
		logging.Error("start failed: %v", err)
	}
	defer g.Stop()

	c, err := nbio.Dial("unix", addr)
	if err != nil {
		logging.Error("dial failed: %v", err)
	}

	if _, err = g.AddConn(c); err != nil {
		logging.Error("failed to add conn: %v", err)
	}

	if _, err = c.Write(buf); err != nil {
		logging.Error("writing to conn failed: %v", err)
	}

	select {
	case <-ctx.Done():
		logging.Error("timeout")
	case <-done:
		logging.Info("success")
	}
}
```

