	}

	controller := obj.Path(`Controllers.0.Controller`)
	_, err = controller.Array("Ports")
	if err != nil {
		panic(err)
	}
	err = controller.ArrayAppendP(1234, "Ports")
	if err != nil {
		panic(err)
	}
	objStr, err := obj.MarshalJSON()
	if err != nil {
		panic(err)
	}
	if string(objStr) != `{"Controllers":[{"Controller":{"Key":"Value","Ports":[1234]}}]}` {
		panic(string(objStr))
	}
}
```

