The Code here
```c++
#include<rapidjson/prettywriter.h>
#include<rapidjson/ostreamwrapper.h>
#include <iostream>

void showJosnDoc(const rapidjson::Document& toshow)
{
	rapidjson::OStreamWrapper out(std::cout);
	rapidjson::PrettyWriter<rapidjson::OStreamWrapper> writer(out);
	std::cout << "\n";
	toshow.Accept(writer);
	std::cout << "\n";
}
rapidjson::Document afun(const rapidjson::Document &d) {

	

	rapidjson::Document dd(rapidjson::kObjectType);
	dd.AddMember("msg", "Hi", dd.GetAllocator());
	dd.AddMember("code", 0, dd.GetAllocator());
	rapidjson::Document cp;
	cp.CopyFrom(d, cp.GetAllocator());
	dd.AddMember("data", cp, dd.GetAllocator());
	std::cout << "pos1 afun\n";
	showJosnDoc(dd);	
	std::cout << "pos2 afun\n";
	showJosnDoc(dd);
	return dd;
}

int main() {

	JSONData indata(rapidjson::kObjectType);

	indata.AddMember("n", 20, indata.GetAllocator());//only this one will lost
	indata.AddMember("info", "hello", indata.GetAllocator());
	showJosnDoc(indata); //comment this will be ok , it seems that I CAN‘T watch it
	rapidjson::Document vb = afun(indata);
	std::cout << "pos3 main\n";
	showJosnDoc(vb);

	return 0;
}
```

