I am trying to use IndexOfAnyExcept in https://github.com/dotnet/runtime/blob/9a138bf5cb4a9055e4e0370a3eb3883514555fcd/src/libraries/Common/src/System/IO/PathInternal.Windows.cs#L381-L392 and I see bad performance if argument is short ("a" or "a ") - IndexOfAnyExcept is 1.44 times slower than the simple loop.

<details>
<summary>Test code</summary>
using BenchmarkDotNet.Attributes;
using BenchmarkDotNet.Running;

using System;
using System.Collections.Generic;
using System.Runtime.Intrinsics;
using System.Numerics;

[MemoryDiagnoser(displayGenColumns: false)]
[DisassemblyDiagnoser]
[HideColumns("Error", "StdDev", "Median", "RatioSD")]
public partial class Program
{
    static void Main(string[] args)
    {
        Console.WriteLine(Vector<ushort>.Count);
        Console.WriteLine(Vector.IsHardwareAccelerated);
        Console.WriteLine(Vector128.IsHardwareAccelerated);
        Console.WriteLine(Vector256.IsHardwareAccelerated);

        BenchmarkSwitcher.FromAssembly(typeof(Program).Assembly).Run(args);
    }

        public IEnumerable<object> Data()
        {
            yield return "";
            yield return "_";
            yield return "a";
            yield return "a_";
            yield return "_a";
            yield return "___a";
            yield return "_______a";
            yield return "_______________a";
        }

        [Benchmark(Baseline = true)]
        [ArgumentsSource(nameof(Data))]
        public bool IsEffectivelyEmpty(ReadOnlySpan<char> path)
        {
            if (path.IsEmpty)
                return true;

            foreach (char c in path)
            {
                if (c != '_')
                    return false;
            }
            return true;
        }

        [Benchmark]
        [ArgumentsSource(nameof(Data))]
        public bool IsEffectivelyEmpty2(ReadOnlySpan<char> path)
        {
            //if (path.IsEmpty)
            //    return true;

            //if (path[0] != '_')
            //    return false;

            if (path.Length < Vector<ushort>.Count)
            {
                foreach (char c in path)
                {
                    if (c != '_')
                        return false;
                }
                return true;
            }

            return path.IndexOfAnyExcept('_') < 0;
        }

        [Benchmark]
        [ArgumentsSource(nameof(Data))]
        public bool IsEffectivelyEmpty2a(ReadOnlySpan<char> path) => path.IndexOfAnyExcept('_') < 0;
}
</details>

