startup code inside of plugin project
```
public class Startup : IDisposable
    {
        private const string DefaultEnviroementName = "Development";
        IConfigurationRoot _configuration;
        ServiceProvider? _serviceProvider;

        public ServiceProvider ServiceProvider { get { return _serviceProvider; } }
        public Startup(Action<IServiceCollection, IConfiguration> deligate)
        {
            ////register Configuration
            /********************************/
            var environment = Environment.GetEnvironmentVariable("ASPNET_ENVIROMENT") ??
                            Environment.GetEnvironmentVariable("ASPNETCORE_ENVIRONMENT") ??
                            DefaultEnviroementName;


            string LocalPath = Path.GetDirectoryName(Assembly.GetExecutingAssembly().Location);

            var builder = new ConfigurationBuilder()
                .SetBasePath(LocalPath)
                .AddJsonFile($"appsettings.json", true, false)
                .AddJsonFile($"appsettings.{environment}.json", optional: true, reloadOnChange: false);

            _configuration = builder.Build();
            /********************************/

            var services = new ServiceCollection();
            deligate(services, _configuration);
            _serviceProvider = services.BuildServiceProvider();
        }

        public void Dispose()
        {
            if (_serviceProvider != null)
                _serviceProvider.Dispose();

            foreach (var provider in _configuration.Providers.OfType<JsonConfigurationProvider>())
            {
                if (provider.Source.FileProvider is PhysicalFileProvider pfp)
                {
                    pfp.Dispose();
                }
            }
        }
    }
```


PluginLoadContext 
```
 public class PluginLoadContext : AssemblyLoadContext
    {
        private AssemblyDependencyResolver _resolver;
        private string _path;
        public PluginLoadContext() : base(isCollectible: true)
        { }

        public PluginLoadContext(string pluginPath) : base(isCollectible: true)
        {
            _path = pluginPath;
            _resolver = new AssemblyDependencyResolver(pluginPath);
        }

        protected override Assembly Load(AssemblyName assemblyName)
        {
            if (_resolver != null)
            {
                string assemblyPath = _resolver.ResolveAssemblyToPath(assemblyName);
                if (assemblyPath != null)
                {
                    return LoadFromAssemblyPath(assemblyPath);
                }
            }
            return null;
        }

        protected override IntPtr LoadUnmanagedDll(string unmanagedDllName)
        {
            string libraryPath = _resolver.ResolveUnmanagedDllToPath(unmanagedDllName);
            if (libraryPath != null)
            {
                return LoadUnmanagedDllFromPath(libraryPath);
            }

            return IntPtr.Zero;
        }

        public IEnumerable<T> GetImplementations<T>()
        {
            var type = typeof(T);
            var assName = AssemblyName.GetAssemblyName(_path);
            var assembly = LoadFromAssemblyName(assName);
            var types = assembly.GetTypes().ToList();
            var other = new List<Type>();
            if (type.IsAbstract && !type.IsInterface)
            {
                other = types.Where(m => m.IsClass && !m.IsAbstract
                                        && m.IsSubclassOf(type)
                                ).ToList();
            }
            else
            {
                other = types.Where(t => type.IsAssignableFrom(t)).ToList();
            }
            return other
                .Select(t => Activator.CreateInstance(t))
                .Cast<T>();

        }

    }
```
