  [TestClass]
    public class UnitTest1
    {
        [TestMethod]
        public async Task TestMethod1Async()
        {
            var directory = Directory.GetCurrentDirectory();
            var scrFolder = "src";
            var indexOfSrc = directory.IndexOf(scrFolder);
            var srcBaseDirectory = directory.Substring(0, indexOfSrc + scrFolder.Length);
            var pluginFolder = Path.Combine(srcBaseDirectory, "_Plugins");

            var plugins = Directory.GetDirectories(pluginFolder);

            var emailPlugin = plugins.Where(x => x.Contains("DatabasePlugin")).Single();
            //var emailPlugin = plugins.Where(x => x.Contains("EmailPlugin")).Single();

            for (int x = 0; x < 10000; x++)
            {
                var folderName = Path.GetFileName(emailPlugin);
                var fullpath = Path.Combine(emailPlugin, folderName + ".dll");

                var hostAlcWeakRef = await ExecuteAndUnload(fullpath);
            
                if (x % 10 ==0)
                {
                    // Poll and run GC until the AssemblyLoadContext is unloaded.
                    // You don't need to do that unless you want to know when the context
                    // got unloaded. You can just leave it to the regular GC.
                    for (int i = 0; hostAlcWeakRef.IsAlive && (i < 10); i++)
                    {
                        GC.Collect();
                        GC.WaitForPendingFinalizers();
                    }
                }
                Console.WriteLine($"Unload success: {!hostAlcWeakRef.IsAlive}");
            }

        }


        [MethodImpl(MethodImplOptions.NoInlining)]
        async Task<WeakReference>  ExecuteAndUnload(string assemblyPath)
        {
            // Create the unloadable HostAssemblyLoadContext
            var alc = new PluginLoadContext(assemblyPath);

            // Create a weak reference to the AssemblyLoadContext that will allow us to detect
            // when the unload completes.
            var alcWeakRef = new WeakReference(alc);

            // Load the plugin assembly into the HostAssemblyLoadContext.
            // NOTE: the assemblyPath must be an absolute path.
            //Assembly a = alc.LoadFromAssemblyPath(assemblyPath);

            // Get the plugin interface by calling the PluginClass.GetInterface method via reflection.
            var componentUI = alc.GetImplementations<BaseComponentUI>().Single();
            var plugin = alc.GetImplementations<IParadoxComponent>().Single();

            // Now we can call methods of the plugin using the interface
            await plugin.ExcuteAsync();
            
            // This initiates the unload of the HostAssemblyLoadContext. The actual unloading doesn't happen
            // right away, GC has to kick in later to collect all the stuff.
            alc.Unload();

            return alcWeakRef;
        }

    }
```


